INSERT INTO `ml_article` VALUES ('68', '4', '2013-05-25 【代办常识】关于预防社保卡诈骗的紧急通知', '', '', '<p style=\"padding: 0px;margin-top: 7px;margin-bottom: 7px;margin-left: 28px;color: rgb(81, 81, 81);font-family: 宋体;font-size: 14px;white-space: normal;line-height: 24px;background-color: rgb(243, 247, 255)\"><span style=\"padding: 0px; font-size: 16px; color: rgb(0, 0, 0);\"></span></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">近期，不法分子利用社保卡进行诈骗的事件屡有发生。甚至有市民接到声讯电话，称&quot;社保卡出现异常</span></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">，将有被停卡的危险&quot;，继而骗取市民信息和钱财。在此，人力社保局提醒您：请妥善保管您的社保卡</span></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">，不要轻信陌生电话、短信，更不要按其提示转账汇款，以免上当受骗。此外社保卡没有透支功能。</span></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">为保障您和他人的安全，参保人接到此类诈骗电话时，请提高警惕，核实来电人信息，切实加强防范，</span></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">谨防上当受骗。若确实发生被欺诈情况，请及时与公安机关联系。</span></p><p><br/></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">【通知】 关于城镇居民基本医疗保险有关问题的通知</span></p><p><br/></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">各区（县）人力资源和社会保障局、财政局，各定点医疗机构：</span></p><p><br/></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">为进一步推进医疗保险改革，完善城镇居民基本医疗保险制度，根据《北京市人民政府关于印发北京市</span></p><p><span style=\"font-size: 16px;\">城镇居民基本医疗保险办法的通知》（京政发〔2010〕38号）和《北京市城镇居民基本医疗保险办法实</span><br/></p><p><span style=\"font-size: 16px;\">施细则》（京人社医发〔2010〕287号），经研究，现就调整城镇居民基本医疗保险住院报销比例和最</span><br/></p><p><br/></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">高支付限额的有关问题通知如下：</span></p><p><br/></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">一、参加城镇居民基本医疗保险的城镇老年人和无业居民，住院报销比例由60%调整为70%，在一个医疗</span></p><p><span style=\"font-size: 16px;\">保险年度内，住院最高支付限额由15万元调整为17万元。</span><br/></p><p><br/></p><p><span style=\"font-size: 16px; color: rgb(0, 0, 0);\">二、本通知自2013年1月1日起执行。</span></p><p><span style=\"padding: 0px; font-family: 宋体; font-size: 16px; color: rgb(0, 0, 0);\"></span><br/></p><p><br/></p>', '', '', '', '', '1', '1445477978', '1445506014', '3', '10');
